import { Grid, Typography, Button, Box } from '@mui/material';
import React from 'react';
import image from '../../assets/image.png';
import HomeRepairServiceIcon from '@mui/icons-material/HomeRepairService';

const Intro = () => {
    return (
        <Grid
            container
            sx={{
                position: 'relative',
                backgroundColor: 'rgb(30, 30, 30)',
                px: { md: 10, xs: 2 },
                height: { xs: '80vh', md: '70vh' },
                overflow: 'hidden',
            }}
        >
            {/* Image (background on mobile, side image on desktop) */}
            <Grid
                item
                xs={12}
                md={5}
                sx={{
                    position: { xs: 'absolute', md: 'relative' },
                    top: 0,
                    left: 0,
                    width: { xs: '100%', md: 'auto' },
                    height: { xs: '100vh', md: '100%' },
                    zIndex: 2,
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                }}
            >
                <Box
                    component="img"
                    src={image}
                    alt="Sahana NV"
                    sx={{
                        width: '100%',
                        height: '100%',
                        objectFit: { xs: 'cover', md: 'contain' },
                        filter: { xs: 'brightness(0.7)', md: 'none' }, // darken image on mobile for overlay contrast
                    }}
                />
            </Grid>

            {/* Text (desktop left, mobile overlay) */}
            <Grid
                item
                xs={12}
                md={7}
                sx={{
                    position: { xs: 'relative', md: 'static' },
                    zIndex: 2,
                    color: 'white',
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'center',
                    alignItems: { xs: 'center', md: 'flex-start' },
                    textAlign: { xs: 'center', md: 'left' },
                    px: { xs: 3, md: 10 },
                    py: { xs: 4, md: 0 },
                    height: { xs: '100vh', md: 'auto' },
                    backgroundColor: { xs: 'rgba(0,0,0,0.5)', md: 'transparent' },
                }}
            >
                <Typography variant="h5" sx={{ color: 'gray', mb: 1 }}>
                    Hello,
                </Typography>
                <Typography
                    variant="h2"
                    sx={{
                        fontWeight: 'bold',
                        lineHeight: 1.2,
                        fontSize: { xs: '2.5rem', md: '3.75rem' }, // responsive font size
                    }}
                >
                    I'm <span style={{ color: 'yellow' }}>Sahana NV</span>
                    <br />
                    Website Designer
                </Typography>
                <Typography
                    sx={{
                        mt: 2,
                        color: 'lightgray',
                        fontSize: { xs: '1rem', md: '1.125rem' }, // responsive font size
                        maxWidth: { md: '500px' },
                    }}
                >
                    I am a skilled web designer with experience in creating
                    visually appealing and user-friendly websites.
                </Typography>
                <Button
                    variant="contained"
                    sx={{
                        mt: 3,
                        color: 'black',
                        backgroundColor: 'white',
                        textTransform: 'none',
                        gap: 1,
                        fontSize: { xs: '1rem', md: 'large' },
                        borderRadius: '30px',
                        padding: '8px 20px',
                        '&:hover': {
                            backgroundColor: 'yellow',
                            color: 'black',
                        },
                        alignSelf: { xs: 'center', md: 'flex-start' },
                    }}
                >
                    <HomeRepairServiceIcon />
                    Hire Me
                </Button>
            </Grid>
        </Grid>
    );
};

export default Intro;
