import React from 'react';
import { Typography, Grid, useMediaQuery, Container } from '@mui/material';
import CheckCircleOutlineIcon from '@mui/icons-material/CheckCircleOutline';
import GroupIcon from '@mui/icons-material/Group';
import BusinessCenterIcon from '@mui/icons-material/BusinessCenter';
import EmojiEventsIcon from '@mui/icons-material/EmojiEvents';

const Experince = () => {
    const isTablet = useMediaQuery('(min-width:601px) and (max-width:1024px)');

    const stats = [
        {
            icon: <CheckCircleOutlineIcon sx={{ fontSize: { xs: 30, md: 40 }, color: 'black' }} />,
            label: 'Completed Projects',
        },
        {
            icon: <GroupIcon sx={{ fontSize: { xs: 30, md: 40 }, color: 'black' }} />,
            label: 'Satisfied Customers',
        },
        {
            icon: <BusinessCenterIcon sx={{ fontSize: { xs: 30, md: 40 }, color: 'black' }} />,
            label: 'Expert Employees',
        },
        {
            icon: <EmojiEventsIcon sx={{ fontSize: { xs: 30, md: 40 }, color: 'black' }} />,
            label: 'Awards Won',
        },
    ];

    return (
        <Container
            maxWidth={false}
            sx={{
                backgroundColor: 'lightgrey',
                py: { xs: 2, md: 4 },
                width: '90vw',
            }}
        >
            <Grid
                container
                spacing={{ xs: 2, md: 4 }}
                justifyContent="center"
                alignItems="center"
            >
                {stats.map((stat, index) => (
                    <Grid
                        item
                        xs={6}
                        sm={3}
                        md={3}
                        key={index}
                        sx={{
                            textAlign: 'center',
                            fontSize: isTablet ? '1.5rem' : 'initial',
                            transform: isTablet ? 'scale(1.05)' : 'initial',
                        }}
                    >
                        {stat.icon}
                        <Typography
                            variant="subtitle1"
                            sx={{
                                color: 'black',
                                fontSize: { xs: '0.9rem', md: '1rem' },
                                mt: 1,
                            }}
                        >
                            {stat.label}
                        </Typography>
                    </Grid>
                ))}
            </Grid>
        </Container>
    );
};

export default Experince;
