import React, { useState } from 'react';
import { Grid, Card, CardContent, Typography, Box, CardMedia } from '@mui/material';
import princy from '../assets/cards image/princy.png';
import image2 from '../assets/cards image/princy.png';
import image3 from '../assets/cards image/princy.png';

const imageList = [princy, image2, image3]; // Array of image sources
const names = ["Ramanuj", "Shankari", "Kumar"]; // Array of names
const messages = [
  "Ramanuj believes in empowering students with the right knowledge and skills to thrive in a digital world.",
  "Shankari emphasizes holistic development and encourages students to embrace technology creatively.",
  "Kumar advocates for innovation and lifelong learning to succeed in a competitive landscape."
]; // Array of unique messages

const Cards = () => {
  const [activeCard, setActiveCard] = useState(null);

  const toggleCard = (index) => {
    setActiveCard(activeCard === index ? null : index);
  };

  return (
    <Grid
      container
      spacing={6}
      sx={{ background: 'linear-gradient(to top,#ffffff, #D9EEE1)', height: '100vh', padding: 4, mt: 1 }}
    >
      <Grid item xs={12}>
        <Typography variant="h2" align="center" sx={{ fontWeight: 600 }}>
          Principal Message
        </Typography>
      </Grid>
      {imageList.map((image, index) => (
        <Grid item xs={12} sm={4} key={index}>
          {/* Main Card */}
          <Card
            sx={{
              background: 'yellow',
              height: '60%',
              cursor: 'pointer',
              transition: 'all 0.3s ease-in-out',
              ...(activeCard === index && {
                transform: 'scale(1.1)',
                zIndex: 2,
              }),
            }}
            onClick={() => toggleCard(index)}
          >
            <CardMedia>
              <img
                src={image}
                alt={`Image ${index + 1}`}
                style={{ height: '80%', width: '95%', objectFit: 'cover' }}
              />
            </CardMedia>
            <CardContent sx={{ height: '80px' }}>
              <Typography variant="h5" align="left" sx={{ fontWeight: 600 }}>
                {names[index]}
              </Typography>
            </CardContent>
          </Card>

          {/* Additional Content */}
          {activeCard === index && (
            <Card
              sx={{
                backgroundColor: 'lightgreen',
                marginTop: 8,
                padding: 2,
                ml:5,
                width: '100%',
                transform: 'scale(1.2)',
                zIndex: 3,
                transition: 'all 0.3s ease-in-out',
              }}
            >
              <CardContent>
                <Typography variant="body1">{messages[index]}</Typography>
              </CardContent>
            </Card>
          )}
        </Grid>
      ))}
    </Grid>
  );
};

export default Cards;
