import React, { useState } from 'react';
import { Grid, Box, Typography, CardMedia, Card, CardContent } from '@mui/material';
import banner1 from '../assets/WhatsApp Image 2024-11-15 at 8.28.08 PM.jpeg'; // Import your image here
import banner2 from '../assets/cloud.png'

const cardsData = [
  {
    image: banner1,
    text: ' This is detailed information about the first card. The content should slide up when the user hovers over the "More About" block.',
    blockText: 'Siddaganga Colleges of Management',
    courses: <span> Courses</span>, 

  },
  {
    image: banner1,
    text: 'Card 2 Content: Detailed content for the second card. Hovering over the "More About" section will reveal this text.',
    blockText: 'Siddaganga PU College',

  },
  {
    image: banner1,
    text: 'Card 3 Content: Information for the third card. Hover to reveal more details below the "More About" section.',
    blockText: 'Siddaganga School',

  },
];

const HoverCards = () => {
  const [hoveredCard, setHoveredCard] = useState(null);

  return (
    <Grid container spacing={4} sx={{ padding: '20px', backgroundImage: `url(${banner2})` }}>
      {/* Title Section */}
      <Grid item xs={12}>
      <Typography variant='h3' sx={{fontWeight:600, fontsise:'30px' ,mt:1, color:'purple', textAlign:'center'}}>
                Accademics 
                </Typography>
      </Grid>

      {/* Cards Section */}
      {cardsData.map((card, index) => (
        <Grid item xs={12} sm={6} md={4} key={index}>
          <Card
            sx={{
              position: 'relative',
              overflow: 'hidden',
              height: '100%',
              borderRadius: '8px',
              boxShadow: '6px 4px 8px rgba(0, 0, 0, 0.8)',
              cursor: 'pointer',
            }}
            onMouseEnter={() => setHoveredCard(index)}
            onMouseLeave={() => setHoveredCard(null)}
          >
            {/* Card Image */}
            <CardMedia
              component="img"
              height="400"
              image={card.image}
              alt={`Card ${index + 1}`}
              sx={{ objectFit: 'cover' }}
            />

            {/* Card Content */}
            <CardContent
              sx={{
                position: 'absolute',
                bottom: 0,
                left: 0,
                width: '100%',
                height: hoveredCard === index ? '160px' : '40px', // Increase height on hover
                backgroundColor: '#bbdefb',
                color: '#000',
                display: 'flex',
                flexDirection: 'column',
                alignItems: hoveredCard === index ? 'flex-start' : 'left',
                justifyContent: 'center',
                // padding: hoveredCard === index ? '20px 20px' : '5px',
                transition: 'all 0.3s ease-in-out',
              }}
            >
              <Typography
                sx={{
                  fontWeight: 'bold',
                  fontSize: '25px',
                }}
              >
                {card.blockText}
              </Typography>

              {/* Hidden Content inside the same block (visible on hover) */}
              {hoveredCard === index && (
                <Box
                  sx={{
                    mt: 2,
                    color: 'black',
                    fontSize: '14px',
                    // fontWeight: 'normal',
                    transition: 'all 0.3s ease-in-out',
                    textAlign: 'center',
                    width:'90%',
                    height:'50vh'
                  }}
                >
                  <Typography textAlign={'left'}>{card.text}</Typography>
                  <Typography textAlign={'left'}>{card.courses}</Typography>
                </Box>
              )}
            </CardContent>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
};

export default HoverCards;
