import { Grid, Typography } from '@mui/material'
import React from 'react'
import banner1 from '../assets/WhatsApp Image 2024-11-15 at 8.28.08 PM.jpeg';

const About = () => {
    return (
        <Grid container
            sx={{
                backgroundColor: '#f2f6fc',
                display: 'flex',
                width:'100%',
                height:'100%',
                padding:'50px  30px',
                mt:{xs:-13, sm:-20}
            }}>
            <Grid item xs={12} md={6}>
                <Typography variant='h2'>
                About us
                </Typography>

            </Grid>
            <Grid item xs={12} md={5} sx={{ alignItems: 'flex-end' , }}>
                <img
                    src={banner1}
                    alt={banner1}
                    style={{ width: '100%', height: '100%' , borderRadius:'2%'}}  // Adjust image height for tablet
                />
            </Grid>

        </Grid>
    )
}

export default About
