import React from 'react';
import { Box, useMediaQuery, useTheme } from '@mui/material';
import Slider from 'react-slick';
import ArrowCircleLeftRoundedIcon from '@mui/icons-material/ArrowCircleLeftRounded';
import ArrowCircleRightRoundedIcon from '@mui/icons-material/ArrowCircleRightRounded';
import banner1 from '../assets/WhatsApp Image 2024-11-15 at 8.28.08 PM.jpeg';

// Single Slide Data
const slidesData = [
  {
    title: "Digital Marketing Courses - Strategy and Resources",
    description: "Unlock the full potential of digital marketing power with comprehensive training that equips you to thrive online.",
    image: banner1,
    buttonText: "Explore More",
    path: '/digital-marketing',
  },
];

const Arrow = ({ className, style, onClick, direction }) => {
  const isMobile = useMediaQuery('(max-width:600px)');
  return (
    <Box
      className={className}
      sx={{
        ...style,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        borderRadius: '50%',
        cursor: 'pointer',
        zIndex: 1,
        width: '40px',
        height: '40px',
        position: 'absolute',
        top: '50%',
        transform: 'translateY(-50%)',
        [direction === 'left' ? 'left' : 'right']: isMobile ? '10px' : '30px',
      }}
      onClick={onClick}
    >
      {direction === 'left' ? <ArrowCircleLeftRoundedIcon /> : <ArrowCircleRightRoundedIcon />}
    </Box>
  );
};

const Banner = () => {
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('md'));
  const isTablet = useMediaQuery(theme.breakpoints.between('sm', 'md'));

  // Slider settings to show a single image
  const sliderSettings = {
    dots: true,
    infinite: true,
    speed: 800,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
    nextArrow: <Arrow direction="right" />,
    prevArrow: <Arrow direction="left" />,
  };

  return (
    <Box
      sx={{
        width: '100%',
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        height: isMobile ? '200px' : isTablet ? '300px' : '200px', // Fixed height based on screen size
        position: 'relative',
      }}
    >
      {/* Slider Component */}
      <Slider {...sliderSettings}>
        {slidesData.map((slide, index) => (
          <Box key={index} sx={{ width: '100%' }}>
            <img
              src={slide.image}
              alt={slide.title}
              style={{
                width: '100%',
                height: '100%',
                objectFit: 'cover', // Ensures the image covers the container
              }}
            />
          </Box>
        ))}
      </Slider>
    </Box>
  );
};

export default Banner;
