import React, { useState } from 'react';
import { Grid, Box, Typography, CardMedia, Card, CardContent, Button } from '@mui/material';
import banner1 from '../assets/WhatsApp Image 2024-11-15 at 8.28.08 PM.jpeg'; // Import your image here
import banner2 from '../assets/cloud.png';
import LocalLibraryIcon from '@mui/icons-material/LocalLibrary';
import LocationOnIcon from '@mui/icons-material/LocationOn';

const cardsData = [
  {
    image: banner1,
    text: ' Provides top-notch Instruction with an Emphasis on fostering the next wave of technological and business leaders. ',
    blockText: 'Siddaganga Colleges of Management and Science',
    icon:<LocalLibraryIcon sx={{mt:2 ,fontSize: 15}}/>,
    courses: ' Courses : BCOM , BCA', 

   location:'42, Lakshmipura Rd, Madanayakahalli, Bengaluru, Siddanahosahalli, Karnataka 562162'
  },
  {
    image: banner1,
    text: 'Provides a vibrant learning atmosphere that emphasizes both academic achievement and overall growth Equipping students for success in the future.',
    blockText: 'Siddaganga PU College',
    icon:<LocalLibraryIcon sx={{mt:2 ,fontSize: 15}}/>,
    courses: ' Courses : Commerce , Science', 
    // icon2:<LocationOnIcon sx={{mt:1 ,fontSize: 15}}/>,
    location:'42, Lakshmipura Rd, Madanayakahalli, Bengaluru, Siddanahosahalli, Karnataka 562162'
  },
  {
    image: banner1,
    text: 'We offers a holistic education experience focusing on academic excellence character building and overall development. With state-of-the-art facilities and a nurturing environment we prepare students to excel in every aspect of life.',
    blockText: 'Siddaganga School',
    // icon2:<LocationOnIcon sx={{mt:1 ,fontSize: 15}}/>,
   location:'42, Lakshmipura Rd, Madanayakahalli, Bengaluru, Siddanahosahalli, Karnataka 562162'
  },
];

const HoverCards = () => {
  const [hoveredCard, setHoveredCard] = useState(null);

  return (
    <Grid container spacing={4} sx={{ padding: '20px', backgroundImage: `url(${banner2})` , height:{md:'80vh', xs:'400vh'}}}>
      {/* Title Section */}
      <Grid item xs={12}>
      <Typography variant='h3' sx={{fontWeight:600, fontsise:'30px' ,mt:1, color:'#907394', textAlign:'center'}}>
                Accademics 
                </Typography>
      </Grid>

      {/* Cards Section */}
      {cardsData.map((card, index) => (
        <Grid item xs={12} sm={6} md={4} key={index} mb={7}>
          <Card
            sx={{
              position: 'relative',
              overflow: 'hidden',
              height: '100%',
              borderRadius: '8px',
              boxShadow: '6px 4px 8px rgba(0, 0, 0, 0.8)',
              cursor: 'pointer',
            }}
            onMouseEnter={() => setHoveredCard(index)}
            onMouseLeave={() => setHoveredCard(null)}
          >
            {/* Card Image */}
            <CardMedia
              component="img"
              height="400"
              image={card.image}
              alt={`Card ${index + 1}`}
              sx={{ objectFit: 'cover' }}
            />

            {/* Card Content */}
            <CardContent
              sx={{
                position: 'absolute',
                bottom: 0,
                left: 0,
                width: '100%',
                height: hoveredCard === index ? '320px' : '40px', // Increase height on hover
                backgroundColor: '#bbdefb',
                color: '#000',
                display: 'flex',
                flexDirection: 'column',
                alignItems: hoveredCard === index ? 'flex-start' : 'left',
                justifyContent: 'center',
                // padding: hoveredCard === index ? '20px 20px' : '5px',
                transition: 'all 0.3s ease-in-out',
                borderTopLeftRadius: '30px',
                borderTopRightRadius: '30px',
              }}
            >
              <Typography
                sx={{
                  fontWeight: 'bold',
                  fontSize: '25px',
                }}
              >
                {card.blockText}
              </Typography>

              {/* Hidden Content inside the same block (visible on hover) */}
              {hoveredCard === index && (
                <Box
                  sx={{
                    mt: 2,
                    color: 'black',
                    fontSize: '14px',
                    // fontWeight: 'normal',
                    transition: 'all 0.3s ease-in-out',
                    textAlign: 'center',
                    width:'90%',
                  }}
                >
                  <Typography textAlign={'left'}>{card.text}</Typography>
                  <Typography textAlign={'left'} > {card.icon}{card.courses}</Typography>
                  <Typography textAlign={'left'} ><LocationOnIcon sx={{mt:1 ,fontSize: 15}}/><span style={{ fontWeight: 'bold' }}> Location : </span>
                  {card.icon2}{card.location}</Typography>
                  <Box sx={{height:40}}>
                  <Button variant='contained' sx={{backgroundColor:'#335064', borderRadius:'30px', mt:2, mb:2}}>
                    View More
                  </Button>
                </Box>
                </Box>

              )}
            </CardContent>
          </Card>
        </Grid>
      ))}
    </Grid>
  );
};

export default HoverCards;
